acer {\"height\":\"25px\"} -->\n<div style=\"height:25px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"id\":71,\"sizeSlug\":\"large\",\"linkDestination\":\"none\",\"align\":\"center\"} -->\n<figure class=\"wp-block-image aligncenter size-large\"><img src=\"http://localhost/wordpress/wp-content/themes/hestia/assets/img/feat2.png\" alt=\"\" class=\"wp-image-71\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {\"textAlign\":\"center\",\"level\":5,\"style\":{\"color\":{\"text\":\"#3c4858\"},\"typography\":{\"fontSize\":18}}} -->\n<h5 class=\"wp-block-heading has-text-align-center has-text-color\" style=\"color:#3c4858;font-size:18px\">Assistenza Personalizzata</h5>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"style\":{\"color\":{\"text\":\"#999999\"},\"typography\":{\"fontSize\":14}}} -->\n<p class=\"has-text-align-center has-text-color\" style=\"color:#999999;font-size:14px\">Offriamo un servizio clienti personalizzato e dedicato, garantendo che ogni dettaglio del tuo viaggio sia perfettamente curato e soddisfi le tue aspettative.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:spacer {\"height\":\"25px\"} -->\n<div style=\"height:25px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"id\":72,\"sizeSlug\":\"large\",\"linkDestination\":\"none\",\"align\":\"center\"} -->\n<figure class=\"wp-block-image aligncenter size-large\"><img src=\"http://localhost/wordpress/wp-content/themes/hestia/assets/img/feat3.png\" alt=\"\" class=\"wp-image-72\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {\"textAlign\":\"center\",\"level\":5,\"style\":{\"color\":{\"text\":\"#3c4858\"},\"typography\":{\"fontSize\":18}}} -->\n<h5 class=\"wp-block-heading has-text-align-center has-text-color\" style=\"color:#3c4858;font-si